st.markdown("--------------------------------------------------------------------------------")

st.write("Veuillez s√©lectionner votre m√©thode de recherche : ", unsafe_allow_html=True) 
genre = st.radio( 
    "",
    ('Par Siren', 'Par Nom')) #on lui propose de choisir entre les deux options

st.markdown("--------------------------------------------------------------------------------")

if genre == 'Par Siren': #si l'utilisateur choisit la premi√®re option (par Siren)
    siren = st.text_input("Rechercher par Siren") #on lui demande de rentrer le Siren
    if siren: #si le Siren est rentr√©
        # Alors on affiche les informations de l'entreprise
        try:
            company_info = df_siren[df_siren["siren"] == (siren)].iloc[0] #on r√©cup√®re les informations de l'entreprise
            st.download_button('T√©lecharger les donn√©es structur√©es üì•',(company_info.to_json()),f"Donn√©es{siren}.json",disabled=False)#on propose de t√©l√©charger les donn√©es
            # Afficher les informations de l'entreprise
            st.dataframe(company_info)#on affiche les informations de l'entreprise dans un tableau 
        except IndexError:
            st.write("Siren non r√©pertori√© ou invalide")#si le Siren n'est pas r√©pertori√© ou invalide
        except BaseException:
            st.write("Erreur inconnue")#si une erreur inconnue survient 
else: #genre == 'Par Nom':
    nom = st.selectbox("Rechercher par Nom",['']+pd.unique(df_gerant["nom"]).tolist())#on lui demande de rentrer le nom
    prenom = st.selectbox("Rechercher par Pr√©nom",df_gerant["prenoms"][df_gerant["nom"] == nom])#on lui demande de rentrer le pr√©nom
    date_naissance = st.selectbox("Rechercher par mois & ann√©e de naissance",df_gerant["dateDeNaissance"][df_gerant["nom"] == nom][df_gerant["prenoms"] == prenom])#on lui demande de rentrer la date de naissance qui correspond au nom et au pr√©nom
    if date_naissance:
        # Appeler la fonction get_company_info avec le Nom entr√© 
            gerant_info = df_gerant[df_gerant["nom"] == nom][df_gerant["prenoms"] == prenom][df_gerant["dateDeNaissance"] == date_naissance].iloc[0]#on r√©cup√®re les informations du g√©rant
            
            siren_gerant = set(gerant_info["siren"].split(","))#on r√©cup√®re les siren associ√©s au g√©rant
            st.write("siren(s) associ√©(s) √† ce nom",siren_gerant,)#on affiche les siren associ√©s au g√©rant
            company_info = df_siren[df_siren["siren"].isin(siren_gerant)]#on r√©cup√®re les informations de l'entreprise
            st.dataframe(company_info)#on affiche les informations de l'entreprise dans un tableau
            st.download_button('T√©lecharger les donn√©es structur√©es üì•',(gerant_info.to_json()),f"Donn√©es{nom}.json",disabled=False)#on propose de t√©l√©charger les donn√©es
            
